import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Base from "../core/Base";
import { fetchInfo } from "./searchHelper/FetchInfoHelper";
import { Table } from "react-bootstrap";

const DetailPage = (props) => {
  const [values, setValues] = useState({
    results: [],
    error: "",
  });

  const loc = useLocation();

  useEffect(() => {
    if (loc.companyId) {
      fetchInfo({ id: loc.companyId }).then((data) => {
        if (data.error) {
          setValues({ ...values, results: [], error: "" });
        } else {
          setValues({ ...values, results: data });
        }
      });
    }
  }, []);

  return (
    <Base>
      <div className="row mb-5 tableContent">
        <div className="container">
          <div className="col-md-12 bg-white py-4 px-5">
            <h1 className="mt-4 mb-4">{values.results.market_cap}</h1>
            <div className="">
              <Table striped hover>
                <tbody>
                  <tr>
                    <td> Current Market Price of stock</td>
                    <td>{values.results.current_price}</td>
                    <td>ROCE</td>
                    <td>{values.results.roce} %</td>
                  </tr>
                  <tr>
                    <td>Stock P/E</td>
                    <td>{values.results.stock}</td>
                    <td>ROE</td>
                    <td>{values.results.roe} %</td>
                  </tr>
                  <tr>
                    <td>Dividend Yield</td>
                    <td>{values.results.yeald}</td>
                    <td>Debt to equity</td>
                    <td>{values.results.equity}</td>
                  </tr>
                  <tr>
                    <td>EPS</td>
                    <td>{values.results.eps}</td>
                    <td>Reserves</td>
                    <td>{values.results.reserves}</td>
                  </tr>
                  <tr>
                    <td>Debt</td>
                    <td>{values.results.Debt}</td>
                  </tr>
                </tbody>
              </Table>
            </div>
          </div>
        </div>
      </div>
    </Base>
  );
};

export default DetailPage;
