import React, { useState, useEffect } from "react";
import { fetchSearchResult } from "./searchHelper/SearchHelper";
import { ListGroup } from "react-bootstrap";
import { Link, useHistory, useLocation } from "react-router-dom";


const SearchBox = () => {
  const history = useHistory();
  const [search, setSearch] = useState({
    keyword: "",
    error: false,
    result: [],
    loading: false,
    message: "",
  });

  const { keyword, result } = search;

  const changehandler = (event) => {
    event.preventDefault();
    const handleChangeDebounce = _.debounce((e) => {
      if (e.target.value) {
        setSearch({
          ...search,
          keyword: event.target.value,
          error: false,
          loading: true,
          message: "",
        });
      } 
    }, 1000);
  };

  useEffect(() => {
    if (keyword) {
      fetchSearchResult({ keyword: keyword }).then((data) => {
        if (data.error) {
          setSearch({
            ...search,
            error: data.error,
          });
        } else {
          setSearch({ ...search, result: data });
        }
      });
    } else {
      setSearch({ ...search, result: [] });
    }
  }, [keyword]);

  const clickHandler = (event) => {
    history.push({
      companyId: event.target.getAttribute("value"),
      pathname: "/Detailpage",
    });
  };

  const ListItems = () => (
    <ListGroup>
      {result.map((list, idx) => (
        <li
          key={idx}
          value={list._id}
          onClick={clickHandler}
          className="text-left  list-group-item"
        >
          {list.market_cap}
        </li>
      ))}
    </ListGroup>
  );

  return (
    <div className="col-md-8  mx-auto mt-5 ">
      <input
        type="text"
        className="form-control searchBox"
        placeholder="Search the company ..."
        onChange={changehandler}
        name="search"
      />
      <ListItems />
    </div>
  );
};
export default SearchBox;
