import React, {useState} from "react";

const SearchBox = () => {

    const [search, setSearch] = useState({
        keyword: "",
        error: "",
        loading: false
    })

    const {keyword, error} = search;


    return(
        <div className="col-md-8  mx-auto mt-5 ">
            <input type="text" className="form-control" placeholder="Search the company ..." onChange={(e) => {setSearch({...search, keyword: e.currentTarget.value, error: false})}} />
        </div>
    )

}
 export default SearchBox;
