import React, {useState, useEffect} from "react";
import { fetchSearchResult } from "./searchHelper/SearchHelper"

const SearchBox = () => {

    const [search, setSearch] = useState({
        keyword: "",
        error: false,
        result: {},
        loading: false,
        message: ""
    })

    const { keyword } = search;

    const changehandler = (event) => {
        fetchSearchResult({"keyword": keyword}).then((data) => {
            if(data.error){
                setSearch({...search, error: data.error, success: false})
            }
            else{
                setSearch({...search, error: data.error, result:data, success: true})
            }
        })

    }

    return(
        <div className="col-md-8  mx-auto mt-5 ">
            <input type="text" className="form-control" placeholder="Search the company ..." onChange={changehandler} name="search" />
            {JSON.stringify(search)}
        </div>
    )

}
 export default SearchBox;
