import React, {useState} from "react";
import { fetchSearchResult } from "./searchHelper/SearchHelper"

const SearchBox = () => {

    const [search, setSearch] = useState({
        keyword: "",
        error: false,
        result: {},
        loading: false,
        message: ""
    })

    const { keyword } = search;

    const changehandler = (event) => {
        setSearch({
            ...search, 
            keyword: event.target.value,
            error: false,
            loading: true, 
            message: ""
        }, () => {
            fetchSearchResult(keyword);
        })
    }

    return(
        <div className="col-md-8  mx-auto mt-5 ">
            <input type="text" className="form-control" placeholder="Search the company ..." onChange={changehandler} />
            {JSON.stringify(search)}
        </div>
    )

}
 export default SearchBox;
