import React, {useState, useEffect} from "react";
import { fetchSearchResult } from "./searchHelper/SearchHelper"
import { ListGroup } from 'react-bootstrap';

const SearchBox = () => {

    const [search, setSearch] = useState({
        keyword: "",
        error: false,
        result: {},
        loading: false,
        message: ""
    })

    const { keyword,result } = search;

    const changehandler = (event) => {
        setSearch({
            ...search, 
            keyword: event.target.value,
            error: false,
            loading: true, 
            message: ""
        })
    }

    useEffect(() => {
        if(keyword){
            fetchSearchResult({"keyword": keyword}).then((data) => {
                if(data.error){
                    setSearch({
                        ...search,
                        error: data.error
                    })
                }
                else{
                    setSearch({...search, result:data})
                }
            })

        }
    }, [keyword])


    const listItems = () => (
    <ListGroup>
        {result.map((link, idx) => {
            <ListGroup.Item key={idx}>{link.name}</ListGroup.Item>
        })}

        <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
        <ListGroup.Item>Morbi leo risus</ListGroup.Item>
        <ListGroup.Item>Porta ac consectetur ac</ListGroup.Item>
        <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
    </ListGroup>
    )

    return(
        <div className="col-md-8  mx-auto mt-5 ">
            <input type="text" className="form-control" placeholder="Search the company ..." onChange={changehandler} name="search" />
            <listItems/>
            {keyword ? listItems() : "" }
            {JSON.stringify(search)}
        </div>
    )

}
 export default SearchBox;
