import React, {useState, useEffect} from "react";
import { fetchSearchResult } from "./searchHelper/SearchHelper"
import { ListGroup } from 'react-bootstrap';
import { Link,useHistory, useLocation } from "react-router-dom";

const SearchBox = () => {
    const history = useHistory();
    const [search, setSearch] = useState({
        keyword: "",
        error: false,
        result: [],
        loading: false,
        message: ""
    })

    const { keyword,result } = search;

    const changehandler = (event) => {
        setSearch({
            ...search, 
            keyword: event.target.value,
            error: false,
            loading: true, 
            message: ""
        })
    }

    useEffect(() => {
        if(keyword){
            fetchSearchResult({"keyword": keyword}).then((data) => {
                if(data.error){
                    setSearch({
                        ...search,
                        error: data.error
                    })
                }
                else{
                    setSearch({...search, result:data})
                }
            })

        }
        else{
            setSearch({...search, result:[]})
        }
    }, [keyword])

    const clickHandler = (event) => {
        history.push({
            customNameData: event.target.value,
            pathname: "/Detailpage"
        })
    }

    const ListItems = () => (
    <ListGroup>
        {
            result.map((list,idx) => (
               <ListGroup.Item key={idx} value={list._id} onClick={clickHandler} className="text-left" >{list.name}</ListGroup.Item>
            ))
        }

    </ListGroup>
    )

    return(
        <div className="col-md-8  mx-auto mt-5 ">
            <input type="text" className="form-control" placeholder="Search the company ..." onChange={changehandler} name="search" />
            <ListItems/>
            {JSON.stringify(search)}
        </div>
    )

}
 export default SearchBox;
