import React, {useState} from "react";

const SearchBox = () => {

    const [search, setSearch] = useState({
        keyword: "",
        error: false,
        loading: false
    })

    const {keyword, error} = search;

    const changehandler = (event) => {
        setSearch({
            ...search, 
            keyword: event.target.value,
            error: false
        })
    }

    return(
        <div className="col-md-8  mx-auto mt-5 ">
            <input type="text" className="form-control" placeholder="Search the company ..." onChange={changehandler} />
            {JSON.stringify(search)}
        </div>
    )

}
 export default SearchBox;
